  const [newUser, setnewUser] = useState([]);
  const [mode, setmode] = useState("2");
  const idtrack = (id) => {
    console.log("********************", id);
    axios.put(`http://localhost:5000/update/${id}`, {
      bstatus: mode,
    });
  };
  useEffect(() => {
    axios
      .get("http://localhost:5000/myposts", {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("jwt"), //the token is a variable which holds the token
        },
      })
      .then((Response) => {
        setnewUser(Response.data);
      });
  }, []);
  return (
    <div>
      <table>
        <tr>
          <th>heading</th>
        </tr>
        {newUser.map((newUser) => (
          <tr>
            <td>{newUser.title}</td>
            {/* <td>{newUser.pdate}</td>
            <td>{newUser.authorname}</td>
            <td>{newUser.pyear} </td> */}

            <td>
              {" "}
              <button
                onClick={(key) => {
                  idtrack(newUser._id);
                }}
              >
                changeid
              </button>{" "}
            </td>
          </tr>
        ))}
      </table>
    </div>
  );
};

export default Book;
