import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
export const arraySearch = (array, keyword) => {
  const searchTerm = keyword.toLowerCase();
  return array.filter((value) => {
    return (
      value.name.toLowerCase().match(new RegExp(searchTerm, "g")) ||
      value.address.toLowerCase().match(new RegExp(searchTerm, "g")) ||
      value.gender.toLowerCase().match(new RegExp(searchTerm, "g"))
    );
  });
};
export const Book = () => {
  const navigate = useNavigate();
  const [newUser, setnewUser] = useState([]);
  const [mode, setmode] = useState("2");
  const idtrack = (id) => {
    console.log("********************", id);
    axios.put(`http://localhost:5000/update/${id}`, {
      bstatus: mode,
    });
  };
  const handleOnChange = async (e) => {
    let value = e.target.value;

    let search = await arraySearch(newUser, value);
    setnewUser(search);
  };
  useEffect(() => {
    axios
      .get("http://localhost:5000/myposts", {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("jwt"), //the token is a variable which holds the token
        },
      })
      .then((Response) => {
        setnewUser(Response.data);
      });
  }, []);
  return (
    <div>
      <input
        type="text"
        placeholder="search by name"
        onChange={handleOnChange}
      />
      <table>
        <tr>
          <th>heading</th>
        </tr>
        {newUser.map((newUser) => (
          <tr>
            <td>{newUser.title}</td>
            {/* <td>{newUser.pdate}</td>
            <td>{newUser.authorname}</td>
            <td>{newUser.pyear} </td> */}

            <td>
              {" "}
              <button
                onClick={(key) => {
                  idtrack(newUser._id);
                }}
              >
                changeid
              </button>{" "}
            </td>
          </tr>
        ))}
      </table>
      <div>
        <h3
          onClick={() => {
            navigate("/Addbook");
          }}
        >
          Add Boook
        </h3>
      </div>
    </div>
  );
};
