import React from "react";

const AddBook = () => {
  const [result, setresult] = React.useState();
  const [mode, setmode] = useState("bashir");
  const [newUser, setnewUser] = useState([]);
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();
  const onSubmit = (data) => {
    setresult(JSON.stringify(data));
    axios
      .post("http://localhost:5000/createpost", {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("jwt"), //the token is a variable which holds the token
        },

        data,
      })
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };
  return (
    <div>
      <h1>Add Book here</h1>
      <form onSubmit={handleSubmit(onSubmit)}>
        <input
          type="text"
          placeholder="name"
          {...register("title", { required: true, maxLength: 80 })}
        />

        <input
          type="text"
          placeholder="Author name"
          {...register("authorname", { required: true, pattern: /^\S+@\S+$/i })}
        />
        <input
          type="text"
          placeholder="publication house"
          {...register("phouse", {
            required: true,
          })}
        />
        <input
          type="date"
          placeholder="publication date"
          {...register("pdate", {
            required: true,
          })}
        />
        <input
          type="text"
          placeholder="publication Year"
          {...register("pyear", {
            required: true,
          })}
        />
        <input
          type="text"
          placeholder="Genre"
          {...register("genre", {
            required: true,
          })}
        />
        <input
          type="file"
          placeholder="Choose pic"
          {...register("photo", {
            required: true,
          })}
        />
        <input type="submit" />
      </form>
    </div>
  );
};

export default AddBook;
