import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { BsThreeDotsVertical } from "react-icons/bs";
import library from "./cover.jpg";
const Book = () => {
  const navigate = useNavigate();
  const [oncl, setoncl] = useState(false);
  const [newUser, setnewUser] = useState([]);
  const [mode, setmode] = useState(1);

  const idtrack = (id) => {
    console.log("********************", id);
    axios.put(`http://localhost:5000/update/${id}`, {
      bstatus: mode,
    });
  };

  useEffect(() => {
    axios
      .get("http://localhost:5000/myposts", {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("jwt"), //the token is a variable which holds the token
        },
      })
      .then((Response) => {
        setnewUser(Response.data);
      });
  }, []);
  return (
    <div className="bookdas">
      <div
        className="logout"
        onClick={() => {
          localStorage.clear();
          navigate("/");
        }}
      >
        logout
      </div>

      {newUser.map((newUser, key) => (
        <div className="card">
          <img
            // src={newUser.photo}
            src={library}
            alt="book cover"
          />
          <div className="row11">
            <div> {newUser.title} </div>
            <div> {newUser.authorname} </div>
          </div>
          <div className="row11">
            <div> {newUser.pdate} </div>
            <div> {newUser.pyear} </div>
          </div>
          <div className="row11">
            <div> {newUser.phouse} </div>
            <div> {newUser.genre} </div>
          </div>
          <div className="options">
            <BsThreeDotsVertical
              style={{ fontSize: "24px", color: "black" }}
              onClick={(key) => {
                if (oncl === true) {
                  setoncl(false);
                } else setoncl(true);
              }}
            />
            <div
              className="maincell"
              style={{ display: oncl ? "flex" : "none" }}
            >
              <div
                className="cell"
                onClick={(key) => {
                  setmode(3);
                  idtrack(newUser._id);
                }}
              >
                Read
              </div>
              <div
                className="cell"
                onClick={(key) => {
                  setmode(2);
                  idtrack(newUser._id);
                }}
              >
                completed
              </div>
              <div
                className="cell"
                onClick={(key) => {
                  setmode(1);
                  idtrack(newUser._id);
                }}
              >
                plan to read
              </div>
            </div>
          </div>
        </div>
      ))}

      <div
        className="AddBookb"
        onClick={() => {
          navigate("/Addbook");
        }}
      >
        Add Boook
      </div>
    </div>
  );
};

export default Book;
